mapper.select:
SELECT
-- 级联表的字段 ,jingdian.jingdian_name as jingdianName ,jingdian.jingdian_uuid_number as jingdianUuidNumber ,jingdian.jingdian_photo as jingdianPhoto ,jingdian.jingdian_tese as jingdianTese ,jingdian.jingdian_luxian as jingdianLuxian ,jingdian.jingdian_address as jingdianAddress ,jingdian.jingdian_zhuyishixiang as jingdianZhuyishixiang ,jingdian.jingdian_types as jingdianTypes ,jingdian.jingdian_old_money as jingdianOldMoney ,jingdian.jingdian_new_money as jingdianNewMoney ,jingdian.jingdian_clicknum as jingdianClicknum ,jingdian.jingdian_content as jingdianContent ,jingdian.shangxia_types as shangxiaTypes ,jingdian.jingdian_delete as jingdianDelete ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney
FROM jingdian_commentback a left JOIN jingdian jingdian ON a.jingdian_id = jingdian.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.jingdian_id = #{params.jingdianId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.jingdian_commentback_text like CONCAT('%',#{params.jingdianCommentbackText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.jingdian_id IS NOT NULL
and jingdian.jingdian_name like CONCAT('%',#{params.jingdianName},'%')
and jingdian.jingdian_uuid_number like CONCAT('%',#{params.jingdianUuidNumber},'%')
and jingdian.jingdian_tese like CONCAT('%',#{params.jingdianTese},'%')
and jingdian.jingdian_luxian like CONCAT('%',#{params.jingdianLuxian},'%')
and jingdian.jingdian_address like CONCAT('%',#{params.jingdianAddress},'%')
and jingdian.jingdian_zhuyishixiang like CONCAT('%',#{params.jingdianZhuyishixiang},'%')
and jingdian.jingdian_types = #{params.jingdianTypes}
and jingdian.jingdian_old_money >= #{params.jingdianOldMoneyStart}
and jingdian.jingdian_old_money <= #{params.jingdianOldMoneyEnd}
and jingdian.jingdian_new_money >= #{params.jingdianNewMoneyStart}
and jingdian.jingdian_new_money <= #{params.jingdianNewMoneyEnd}
and jingdian.jingdian_clicknum >= #{params.jingdianClicknumStart}
and jingdian.jingdian_clicknum <= #{params.jingdianClicknumEnd}
and jingdian.jingdian_clicknum = #{params.jingdianClicknum}
and jingdian.jingdian_content like CONCAT('%',#{params.jingdianContent},'%')
and jingdian.shangxia_types = #{params.shangxiaTypes}
and jingdian.jingdian_delete >= #{params.jingdianDeleteStart}
and jingdian.jingdian_delete <= #{params.jingdianDeleteEnd}
and jingdian.jingdian_delete = #{params.jingdianDelete}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
order by a.${params.orderBy} desc
